import React from 'react';
import './App.css';
import { Notifications } from 'react-push-notification';
import addNotification from 'react-push-notification';

function App() {
  const envoyerNotification = () => {
    addNotification({
      title: 'Bienvenue',
      subtitle: 'Ceci est une notification',
      message: 'Vous avez réussi à implémenter les notifications push !',
      theme: 'darkblue',
      native: false // Mettez true pour une notification native du système d'exploitation
    });
  }

  return (
    <div className="App">
      <Notifications />
      <header className="App-header">
        <h1>Ma PWA React</h1>
        <button onClick={envoyerNotification}>Envoyer une notification</button>
      </header>
    </div>
  );
}

export default App;
